import React, { useState, useEffect } from 'react';
import { Account, TransferRequest } from '../types';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';

interface TransferFormProps {
  accounts: Account[];
  onSubmit: (data: TransferRequest) => Promise<void>;
  loading?: boolean;
  error?: string | null;
  initialFromAccount?: Account | null;
  onCancel?: () => void;
}

const TransferForm: React.FC<TransferFormProps> = ({
  accounts,
  onSubmit,
  loading = false,
  error = null,
  initialFromAccount = null,
  onCancel
}) => {
  const [formData, setFormData] = useState<TransferRequest>({
    from_account: initialFromAccount?.name || '',
    to_account: '',
    amount: 0,
    note: ''
  });
  const [validationErrors, setValidationErrors] = useState<{[key: string]: string}>({});
  const [exchangeRate, setExchangeRate] = useState<number | null>(null);
  const [convertedAmount, setConvertedAmount] = useState<number | null>(null);

  const fromAccount = accounts.find(acc => acc.name === formData.from_account);
  const toAccount = accounts.find(acc => acc.name === formData.to_account);

  useEffect(() => {
    if (initialFromAccount) {
      setFormData(prev => ({ ...prev, from_account: initialFromAccount.name }));
    }
  }, [initialFromAccount]);

  useEffect(() => {
    if (fromAccount && toAccount && formData.amount > 0) {
      if (fromAccount.currency !== toAccount.currency) {
        const rates = { KES: 0.0067, USD: 1, NGN: 0.00125 };
        const rate = rates[toAccount.currency as keyof typeof rates] / rates[fromAccount.currency as keyof typeof rates];
        setExchangeRate(rate);
        setConvertedAmount(formData.amount * rate);
      } else {
        setExchangeRate(null);
        setConvertedAmount(null);
      }
    }
  }, [fromAccount, toAccount, formData.amount]);

  const validateForm = (): boolean => {
    const errors: {[key: string]: string} = {};

    if (!formData.from_account) {
      errors.from_account = 'Please select a source account';
    }

    if (!formData.to_account) {
      errors.to_account = 'Please select a destination account';
    }

    if (formData.from_account === formData.to_account) {
      errors.to_account = 'Source and destination accounts must be different';
    }

    if (!formData.amount || formData.amount <= 0) {
      errors.amount = 'Please enter a valid amount';
    }

    if (fromAccount && formData.amount > fromAccount.balance) {
      errors.amount = `Insufficient balance. Available: ${fromAccount.formatted_balance}`;
    }

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    await onSubmit(formData);
  };

  const handleInputChange = (field: keyof TransferRequest, value: string | number) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (validationErrors[field]) {
      setValidationErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const formatCurrency = (amount: number, currency: string): string => {
    const symbols = { KES: 'KSh', USD: '$', NGN: '₦' };
    const symbol = symbols[currency as keyof typeof symbols] || currency;
    return `${symbol} ${amount.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-semibold text-gray-800">Transfer Money</h2>
        {onCancel && (
          <button
            onClick={onCancel}
            className="text-gray-500 hover:text-gray-700"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        )}
      </div>

      {error && <ErrorMessage message={error} />}

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            From Account
          </label>
          <select
            value={formData.from_account}
            onChange={(e) => handleInputChange('from_account', e.target.value)}
            className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-kcb-primary focus:border-transparent ${
              validationErrors.from_account ? 'border-red-500' : 'border-gray-300'
            }`}
            disabled={loading}
          >
            <option value="">Select source account</option>
            {accounts.map(account => (
              <option key={account.name} value={account.name}>
                {account.name} - {account.formatted_balance}
              </option>
            ))}
          </select>
          {validationErrors.from_account && (
            <p className="mt-1 text-sm text-red-600">{validationErrors.from_account}</p>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            To Account
          </label>
          <select
            value={formData.to_account}
            onChange={(e) => handleInputChange('to_account', e.target.value)}
            className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-kcb-primary focus:border-transparent ${
              validationErrors.to_account ? 'border-red-500' : 'border-gray-300'
            }`}
            disabled={loading}
          >
            <option value="">Select destination account</option>
            {accounts
              .filter(account => account.name !== formData.from_account)
              .map(account => (
                <option key={account.name} value={account.name}>
                  {account.name} - {account.formatted_balance}
                </option>
              ))}
          </select>
          {validationErrors.to_account && (
            <p className="mt-1 text-sm text-red-600">{validationErrors.to_account}</p>
          )}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Amount ({fromAccount?.currency || 'Currency'})
          </label>
          <input
            type="number"
            step="0.01"
            min="0"
            value={formData.amount || ''}
            onChange={(e) => handleInputChange('amount', parseFloat(e.target.value) || 0)}
            className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-kcb-primary focus:border-transparent ${
              validationErrors.amount ? 'border-red-500' : 'border-gray-300'
            }`}
            placeholder="Enter amount"
            disabled={loading}
          />
          {validationErrors.amount && (
            <p className="mt-1 text-sm text-red-600">{validationErrors.amount}</p>
          )}
          {fromAccount && (
            <p className="mt-1 text-sm text-gray-500">
              Available balance: {fromAccount.formatted_balance}
            </p>
          )}
        </div>

        {exchangeRate && convertedAmount && toAccount && (
          <div className="bg-blue-50 p-4 rounded-lg">
            <p className="text-sm text-blue-800">
              Exchange Rate: 1 {fromAccount?.currency} = {exchangeRate.toFixed(4)} {toAccount.currency}
            </p>
            <p className="text-sm text-blue-800 font-medium">
              Converted Amount: {formatCurrency(convertedAmount, toAccount.currency)}
            </p>
          </div>
        )}

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Note (Optional)
          </label>
          <textarea
            value={formData.note}
            onChange={(e) => handleInputChange('note', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-kcb-primary focus:border-transparent"
            placeholder="Add a note for this transfer"
            rows={3}
            disabled={loading}
          />
        </div>

        <div className="flex space-x-4">
          <button
            type="submit"
            disabled={loading}
            className="flex-1 bg-kcb-primary text-white py-3 px-6 rounded-lg hover:bg-kcb-primary-dark transition-colors font-medium disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? (
              <div className="flex items-center justify-center">
                <LoadingSpinner size="sm" />
                <span className="ml-2">Processing...</span>
              </div>
            ) : (
              'Transfer Money'
            )}
          </button>

          {onCancel && (
            <button
              type="button"
              onClick={onCancel}
              disabled={loading}
              className="px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors font-medium disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Cancel
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default TransferForm;
