import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import TransferForm from './components/TransferForm';

const App: React.FC = () => {
  const [currentPage, setCurrentPage] = useState('dashboard');

  const renderContent = () => {
    switch (currentPage) {
      case 'transfer':
        return <TransferForm accounts={[]} onSubmit={async () => {}} />;
      case 'transactions':
        return <div className="p-8"><h1 className="text-2xl">Transactions Page</h1></div>;
      case 'history':
        return <div className="p-8"><h1 className="text-2xl">Transaction History Page</h1></div>;
      default:
        return (
          <div className="p-8">
            <h1 className="text-3xl font-bold text-kcb-primary mb-8">
              Treasury Movement Simulator Dashboard
            </h1>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              <div className="bg-white p-6 rounded-lg shadow-lg">
                <h2 className="text-lg font-semibold text-gray-800 mb-2">KES Accounts</h2>
                <p className="text-3xl font-bold text-currency-kes">KSh 225,000.00</p>
                <p className="text-sm text-gray-600">3 accounts</p>
              </div>

              <div className="bg-white p-6 rounded-lg shadow-lg">
                <h2 className="text-lg font-semibold text-gray-800 mb-2">USD Accounts</h2>
                <p className="text-3xl font-bold text-currency-usd">$ 25,000.00</p>
                <p className="text-sm text-gray-600">4 accounts</p>
              </div>

              <div className="bg-white p-6 rounded-lg shadow-lg">
                <h2 className="text-lg font-semibold text-gray-800 mb-2">NGN Accounts</h2>
                <p className="text-3xl font-bold text-currency-ngn">₦ 1,500,000.00</p>
                <p className="text-sm text-gray-600">3 accounts</p>
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <button 
                  onClick={() => setCurrentPage('transfer')}
                  className="bg-kcb-primary text-white px-6 py-3 rounded-lg hover:bg-kcb-secondary transition-colors font-medium"
                >
                  📤 Send Money
                </button>
                <button 
                  onClick={() => setCurrentPage('transactions')}
                  className="bg-kcb-primary text-white px-6 py-3 rounded-lg hover:bg-kcb-secondary transition-colors font-medium"
                >
                  📋 View Transactions
                </button>
                <button 
                  onClick={() => setCurrentPage('history')}
                  className="bg-kcb-primary text-white px-6 py-3 rounded-lg hover:bg-kcb-secondary transition-colors font-medium"
                >
                  🕒 Transaction History
                </button>
              </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      <Sidebar current={currentPage} onNavigate={setCurrentPage} />
      <div className="flex-1 ml-64">
        {renderContent()}
      </div>
    </div>
  );
};

export default App;
