import React, { useState } from 'react';
import Layout from './components/Layout';
import Dashboard from './pages/Dashboard';
import AccountList from './components/AccountList';
import TransferForm from './components/TransferForm';
import TransactionLog from './components/TransactionLog';
import TransferPage from './pages/TransferPage';
import { useAccounts } from './hooks/useAccounts';
import { useTransactions } from './hooks/useTransactions';

const App: React.FC = () => {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const accountsHook = useAccounts();
  const transactionsHook = useTransactions();

  let content;
  switch (currentPage) {
    case 'dashboard':
      content = <Dashboard />;
      break;
    case 'accounts':
      content = (
        <AccountList
          accounts={accountsHook.accounts}
          loading={accountsHook.loading}
          error={accountsHook.error}
          onRetry={accountsHook.refreshAccounts}
          showDetails={true}
        />
      );
      break;
    case 'transfers':
      content = (
        <TransferPage onNavigate={setCurrentPage} />
      );
      break;
    case 'transactions':
      content = (
        <TransactionLog
          transactions={transactionsHook.transactions}
          loading={transactionsHook.loading}
          error={transactionsHook.error}
          onRetry={transactionsHook.refreshTransactions}
        />
      );
      break;
    default:
      content = <Dashboard />;
  }

  return (
    <Layout
      header={null}
      footer={null}
      currentPage={currentPage}
      onNavigate={setCurrentPage}
    >
      {content}
    </Layout>
  );
};

export default App;
