import React, { useState } from 'react';
import Container from './Container';
import Sidebar from './Sidebar';

interface LayoutProps {
  children: React.ReactNode;
  header?: React.ReactNode;
  footer?: React.ReactNode;
  className?: string;
}

const Layout: React.FC<LayoutProps> = ({ 
  children, 
  header, 
  footer, 
  className = '' 
}) => {
  // Navigation state for sidebar
  const [currentPage, setCurrentPage] = useState('dashboard');

  return (
    <div className={`min-h-screen bg-gray-50 flex ${className}`}>
      <Sidebar current={currentPage} onNavigate={setCurrentPage} />
      <div className="flex-1 ml-64 min-h-screen flex flex-col">
        {header && (
          <header className="sticky top-0 z-40">
            {header}
          </header>
        )}
        <main className="flex-1 pb-20">
          <Container maxWidth="xl">
            {children}
          </Container>
        </main>
        {footer && (
          <footer className="fixed bottom-0 left-64 right-0 bg-white border-t border-gray-200 z-40">
            {footer}
          </footer>
        )}
      </div>
    </div>
  );
};

export default Layout;
