import React from 'react';
import { HomeIcon, BanknotesIcon, ArrowsRightLeftIcon, ClockIcon, Cog6ToothIcon, UserCircleIcon } from '@heroicons/react/24/outline';

interface SidebarProps {
  current: string;
  onNavigate: (page: string) => void;
}

const navItems = [
  { key: 'dashboard', label: 'Dashboard', icon: <HomeIcon className="w-6 h-6" /> },
  { key: 'accounts', label: 'Accounts', icon: <BanknotesIcon className="w-6 h-6" /> },
  { key: 'transfers', label: 'Transfers', icon: <ArrowsRightLeftIcon className="w-6 h-6" /> },
  { key: 'transactions', label: 'Transactions', icon: <ClockIcon className="w-6 h-6" /> },
  // { key: 'fx', label: 'FX Rates', icon: <CurrencyDollarIcon className="w-6 h-6" /> },
  // { key: 'reconciliation', label: 'Reconciliation', icon: <AdjustmentsHorizontalIcon className="w-6 h-6" /> },
];

const Sidebar: React.FC<SidebarProps> = ({ current, onNavigate }) => {
  return (
    <aside className="h-screen w-64 bg-kcb-primary text-white flex flex-col fixed left-0 top-0 z-50 shadow-lg">
      <div className="flex items-center px-6 py-6 border-b border-kcb-dark">
        <span className="font-bold text-xl tracking-tight">Treasury</span>
      </div>
      <nav className="flex-1 py-4 space-y-2">
        {navItems.map(item => (
          <button
            key={item.key}
            onClick={() => onNavigate(item.key)}
            className={`
              flex items-center w-full px-6 py-3 text-left transition-colors
              ${current === item.key ? 'bg-kcb-dark text-white font-semibold' : 'hover:bg-kcb-secondary text-white'}
            `}
          >
            <span className="mr-3">{item.icon}</span>
            {item.label}
          </button>
        ))}
      </nav>
      <div className="mt-auto px-6 py-4 border-t border-kcb-dark flex items-center space-x-3">
        <UserCircleIcon className="w-8 h-8 text-kcb-light" />
        <div>
          <p className="font-semibold leading-tight">Treasury Manager</p>
          <p className="text-xs text-kcb-light">Ops Team</p>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
